import { StoreType } from "./constants";
import { INDEXEDDB_EVENT_COUNT_LIMIT, MAX_ATTEMPTS, MEMORYDB_EVENT_COUNT_LIMIT } from "./defaults";
export const createOptionsWithDefaults = (options, storeType) => ({
  logger: options.logger || console,
  useMemory: options.useMemory || false,
  maxAttempts: options.maxAttempts || MAX_ATTEMPTS,
  // This is so that we can customise `maxEventLimit`, primarily for testing.
  maxEventLimit: options.maxEventLimit || (storeType === StoreType.INDEXEDDB ? INDEXEDDB_EVENT_COUNT_LIMIT : MEMORYDB_EVENT_COUNT_LIMIT)
});