import InMemoryStorage from './InMemoryStorage';
import SafeStorage from './SafeStorage'; // eslint-disable-next-line import/no-anonymous-default-export

export default class extends SafeStorage {
  constructor(environmentPrefix) {
    let storageToUse;

    try {
      storageToUse = window.localStorage || InMemoryStorage;
    } catch (err) {
      storageToUse = InMemoryStorage;
    }

    super(storageToUse, environmentPrefix);
  }

}